import React, { useState } from 'react';
import styles from '../styles/navMenu.module.scss';
import classes from 'classnames';

function NavMenu() {



    const [ isOpen, setIsOpen ] = useState(false);

    // toggle the class and state on the nav, triggered onclick
    function handleToggle() {
        setIsOpen(!isOpen);
    };

    return (
            <div className={ classes( styles.navBurger, { [styles.open] : isOpen } ) } onClick={() => { handleToggle() } }>
                <span></span>
                <span></span>
                <span></span>
                <span></span>
            </div> 


    );
};

export default NavMenu;
