import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import styles from '../styles/projectCard.module.scss';
import Button from '../components/Button';

function ProjectCard({ project, projectIDarr }) {

    const navigate = useNavigate();

    const toProjectPage=()=>{
        navigate(`/project/${project.id}`, {state:{projectIDarr}});
    }

    return (
        <article key={project.id} className={styles.card}>
            {/* <Link   to={`/project/${project.id}`}  */}
            <div><a onClick={()=>{toProjectPage()}}
                    className={styles.info} >
                <img src={project.acf.cover_image.url} alt={project.acf.cover_image.alt} />
                <h2>{project.title.rendered}</h2>
                <p>{project.acf.one_liner}</p>
            </a></div>
            <Button url={`/project/${project.id}`} btnText="Learn More" />
        </article>
    );
};

export default ProjectCard;
